DROP TABLE IF EXISTS `raw_power_plants`;
CREATE TABLE `raw_power_plants` (
  `id` int NOT NULL AUTO_INCREMENT COMMENT '主键ID',
  `lng` decimal(10,6) DEFAULT NULL COMMENT '经度坐标',
  `lat` decimal(10,6) DEFAULT NULL COMMENT '纬度坐标',
  `annual_carbon` decimal(10,2) DEFAULT NULL,
  `capacity` int DEFAULT NULL COMMENT '装机容量(MW)',
  `coal_type` varchar(100) DEFAULT NULL,
  `country` varchar(100) DEFAULT NULL COMMENT '所在国家',
  `plant` varchar(255) DEFAULT NULL COMMENT '电站名称',
  `status` varchar(50) DEFAULT NULL COMMENT '运营状态',
  `type` varchar(100) DEFAULT NULL COMMENT '技术类型',
  `retire1` int DEFAULT NULL COMMENT '退役年份预测1',
  `retire2` int DEFAULT NULL COMMENT '退役年份预测2',
  `retire3` int DEFAULT NULL COMMENT '退役年份预测3',
  `start1` int DEFAULT NULL COMMENT '首台机组投产年份',
  `start2` int DEFAULT NULL COMMENT '末台机组投产年份',
  `year1` int DEFAULT NULL COMMENT '数据年份1',
  `year2` int DEFAULT NULL COMMENT '数据年份2',
  `start_label` varchar(200) DEFAULT NULL,
  `region_label` varchar(200) DEFAULT NULL,
  `created_at` datetime DEFAULT CURRENT_TIMESTAMP COMMENT '创建时间',
  `updated_at` datetime DEFAULT CURRENT_TIMESTAMP ON UPDATE CURRENT_TIMESTAMP COMMENT '更新时间',
  `deleted_at` datetime DEFAULT NULL,
  PRIMARY KEY (`id`),
  KEY `idx_country` (`country`),
  KEY `idx_status` (`status`),
  KEY `idx_type` (`type`),
  KEY `idx_location` (`lng`,`lat`),
  KEY `idx_capacity` (`capacity`),
  KEY `idx_carbon` (`annual_carbon`),
  KEY `idx_country_status` (`country`,`status`),
  KEY `idx_status_capacity` (`status`,`capacity` DESC),
  KEY `idx_deleted` (`deleted_at`),
  KEY `idx_plant_name` (`plant`)
) ENGINE=InnoDB AUTO_INCREMENT=5295 DEFAULT CHARSET=utf8mb4 COLLATE=utf8mb4_0900_ai_ci COMMENT='原始电站数据表 - 未经规范化的源数据，用于数据导入和历史追溯';

